import React from "react";
import { useStateContext } from "../context/StateContext";

const OutputPanel = () => {
  const { workflowSteps } = useStateContext();

  const shapeWidth = 200;
  const shapeHeight = 60;
  const verticalSpacing = 100;

  return (
    <div>
      <h3>ETL Workflow</h3>
      {workflowSteps.length === 0 ? (
        <p style={{ color: "#999" }}>No steps added yet.</p>
      ) : (
        <svg width="100%" height={workflowSteps.length * verticalSpacing + 100}>
          <defs>
            <marker
              id="arrow"
              markerWidth="10"
              markerHeight="10"
              refX="5"
              refY="5"
              orient="auto"
            >
              <path d="M0,0 L10,5 L0,10 Z" fill="#000" />
            </marker>
          </defs>

          {workflowSteps.map((step, index) => {
            const x = 100;
            const y = index * verticalSpacing + 50;
            return (
              <g key={index}>
                {/* Rounded Rectangle */}
                <rect
                  x={x}
                  y={y}
                  rx="20"
                  ry="20"
                  width={shapeWidth}
                  height={shapeHeight}
                  fill="#bbdefb"
                  stroke="#1976d2"
                  strokeWidth="2"
                />
                {/* Step Text */}
                <text
                  x={x + shapeWidth / 2}
                  y={y + shapeHeight / 2 + 5}
                  textAnchor="middle"
                  fontSize="14"
                  fill="#000"
                >
                  {step}
                </text>
                {/* Arrow Line to next step */}
                {index < workflowSteps.length - 1 && (
                  <line
                    x1={x + shapeWidth / 2}
                    y1={y + shapeHeight}
                    x2={x + shapeWidth / 2}
                    y2={y + verticalSpacing}
                    stroke="#000"
                    strokeWidth="2"
                    markerEnd="url(#arrow)"
                  />
                )}
              </g>
            );
          })}
        </svg>
      )}
    </div>
  );
};

export default OutputPanel;
