import axios from "axios";

const BASE_URL = "http://localhost:5000"; // Adjust if needed

export const fetchNormalizedTables = async () => {
  const res = await axios.get(`${BASE_URL}/api/normalized_tables`);
  return res.data.tables;
};

export const fetchCodeForStep = async (stepName) => {
  const res = await axios.get(`${BASE_URL}/api/code/${stepName}`);
  return res.data.code;
};

export const validateStep = async (currentSteps, nextStep) => {
  const res = await axios.post(`${BASE_URL}/api/validate_step`, {
    currentSteps,
    nextStep,
  });
  return res.data;
};

export const fetchTableHTML = async (tableName) => {
  const res = await axios.get(`${BASE_URL}/api/table_html/${tableName}`);
  return res.data.html;
};

export const uploadFile = async (file) => {
  const formData = new FormData();
  formData.append("datafile", file);
  const res = await axios.post(`${BASE_URL}/upload`, formData);
  return res.data;
};
