// src/components/MessagePanel.jsx
import React from 'react';
import { useStateContext } from '../context/StateContext';
import { useNavigate } from 'react-router-dom';
import { FaTable, FaProjectDiagram, FaSitemap } from 'react-icons/fa';

const MessagePanel = () => {
  const {
    messages,
    normalizedTables,
    setSelectedTable,
    erDiagramGenerated,
    fdGenerated, // ✅ NEW
  } = useStateContext();

  const navigate = useNavigate();

  const handleTableClick = async (table) => {
  const filename = table.endsWith('.csv') ? table : `${table}.csv`;

  try {
    const response = await fetch(`/api/table/${filename}`);
    const data = await response.json();

    if (data.rows && data.rows.length > 0) {
      console.log("Table data fetched:", data.rows);
      setSelectedTableData(data.rows);
      setSelectedTable(filename); // or setSelectedTable(table) as needed
    } else {
      console.warn("No rows found or empty table");
      setSelectedTableData([]);
    }
  } catch (error) {
    console.error("Error fetching table data:", error);
    setSelectedTableData([]);
  }
};


  const handleERDiagramClick = () => {
    navigate('/er-diagram');
  };

  const handleFDPageClick = () => {
    navigate('/fd-view');
  };

  const stripExtension = (filename) => filename.split('.')[0];

  return (
    <div className="message-panel">
      <div className="messages-list">
        {messages.map((msg, index) => (
          <div key={index} className="message-item">
            {typeof msg === 'string' ? msg : JSON.stringify(msg)}
          </div>
        ))}
      </div>

      <div className="normalized-tables">
        {normalizedTables.map((table, index) => (
          <button key={index} onClick={() => handleTableClick(table)}>
            <FaTable style={{ marginRight: '6px' }} />
            {stripExtension(table)}
          </button>
        ))}

        {erDiagramGenerated && (
          <button onClick={handleERDiagramClick} className="er-diagram-button">
            <FaProjectDiagram style={{ marginRight: '6px' }} />
            Show ER Diagram
          </button>
        )}

        {fdGenerated && (
          <button onClick={handleFDPageClick} className="er-diagram-button">
            <FaSitemap style={{ marginRight: '6px' }} />
            Show Functional Dependencies
          </button>
        )}
      </div>
    </div>
  );
};

export default MessagePanel;
