// src/components/MessagePanel.jsx
import React, { useState } from 'react';
import { useStateContext } from '../context/StateContext';
import { useNavigate } from 'react-router-dom';
import { FaTable, FaProjectDiagram, FaSitemap, FaChevronDown, FaChevronUp } from 'react-icons/fa';

const MessagePanel = () => {
  const {
    messages,
    normalizedTables,
    fetchSelectedTableData,
    addMessage,
    erDiagramGenerated,
    fdGenerated,
  } = useStateContext();

  const navigate = useNavigate();
  const [dropdownOpen, setDropdownOpen] = useState(false);

  const handleTableClick = async (tableName) => {
    try {
      addMessage(`Loading table ${tableName}...`);
      await fetchSelectedTableData(tableName);
      navigate('/table-view');
    } catch (error) {
      console.error('Error fetching table data:', error);
      addMessage(`Error loading table ${tableName}: ${error.response?.data?.error || error.message}`);
    }
  };

  const handleERDiagramClick = () => {
    navigate('/er-diagram');
  };

  const handleFDPageClick = () => {
    navigate('/fd-view');
  };

  return (
    <div className="message-panel">
      <div className="messages-list">
        {messages.map((msg, index) => (
          <div key={index} className="message-item">
            {typeof msg === 'string' ? msg : JSON.stringify(msg)}
          </div>
        ))}
      </div>

      <div className="normalized-tables">
        <div className="dropdown">
          <button
            className="dropdown-toggle"
            onClick={() => setDropdownOpen(!dropdownOpen)}
          >
            <FaTable style={{ marginRight: '6px' }} />
            Normalized Tables
            {dropdownOpen ? <FaChevronUp style={{ marginLeft: '8px' }} /> : <FaChevronDown style={{ marginLeft: '8px' }} />}
          </button>

          {dropdownOpen && (
            <ul className="dropdown-menu">
              {normalizedTables.map((table, index) => (
                <li key={index} onClick={() => handleTableClick(table)}>
                  {(table)}
                </li>
              ))}
            </ul>
          )}
        </div>

        {erDiagramGenerated && (
          <button onClick={handleERDiagramClick} className="er-diagram-button">
            <FaProjectDiagram style={{ marginRight: '6px' }} />
            Show ER Diagram
          </button>
        )}

        {fdGenerated && (
          <button onClick={handleFDPageClick} className="er-diagram-button">
            <FaSitemap style={{ marginRight: '6px' }} />
            Show Functional Dependencies
          </button>
        )}
      </div>
    </div>
  );
};

export default MessagePanel;
