import React, { useEffect, useState } from 'react';
import { useStateContext } from '../context/StateContext';
import DraggableStep from './DraggableStep';
import axios from 'axios';

const OutputPanel = () => {
  const { workflowSteps, setCurrentCode } = useStateContext();
  const [positions, setPositions] = useState({});

  useEffect(() => {
    const initialPositions = {};
    workflowSteps.forEach((step, index) => {
      initialPositions[step] = { x: 50, y: 50 + index * 80 };
    });
    setPositions((prev) => ({ ...prev, ...initialPositions }));
  }, [workflowSteps]);

  const handlePositionChange = (step, pos) => {
    setPositions((prev) => ({ ...prev, [step]: pos }));
  };

  const handleClickStep = async (step) => {
    try {
      const response = await axios.get(`/api/code/${step}`);
      setCurrentCode(response.data.code || '');
    } catch {
      setCurrentCode('Failed to load code');
    }
  };

  const renderLines = () => {
    const lines = [];
    for (let i = 0; i < workflowSteps.length - 1; i++) {
      const from = positions[workflowSteps[i]];
      const to = positions[workflowSteps[i + 1]];
      if (from && to) {
        lines.push(
          <line
            key={`line-${i}`}
            x1={from.x + 60}
            y1={from.y + 20}
            x2={to.x + 60}
            y2={to.y + 20}
            stroke="#bd93f9" // Purple color for lines
            strokeWidth="2"
          />
        );
      }
    }
    return lines;
  };

  return (
    <div className="output-panel">
      <h2>Database Output</h2>
      
      <div className="output-section">
        <h3>Tableau</h3>
        <div className="output-items">
          <div className="output-item">
            <strong>Connect To Copy</strong>
            <div className="connection-status">
              <span className="connection-status-icon connection-active"></span>
              <span>Active Connection</span>
            </div>
          </div>
          <DraggableStep
            step="Get Started"
            position={positions["Get Started"] || { x: 50, y: 150 }}
            onPositionChange={handlePositionChange}
            onClick={handleClickStep}
          />
          <DraggableStep
            step="Not Created"
            position={positions["Not Created"] || { x: 50, y: 230 }}
            onPositionChange={handlePositionChange}
            onClick={handleClickStep}
          />
        </div>
      </div>

      <div className="output-section">
        <h3>Key Detection</h3>
        <div className="output-items">
          <DraggableStep
            step="Reduce Error"
            position={positions["Reduce Error"] || { x: 50, y: 350 }}
            onPositionChange={handlePositionChange}
            onClick={handleClickStep}
          />
        </div>
      </div>

      <svg style={{ position: 'absolute', width: '100%', height: '100%', zIndex: 0 }}>
        {renderLines()}
      </svg>
    </div>
  );
};

export default OutputPanel;
