// src/components/MessagePanel.jsx
import React from 'react';
import { useNavigate } from 'react-router-dom';
import { useStateContext } from '../context/StateContext';
import { FaSitemap } from 'react-icons/fa';
import { PiFunctionLight } from 'react-icons/pi';

const MessagePanel = () => {
  const {
    messages,
    normalizedTables,
    setSelectedTable,
    erDiagramGenerated,
    fdGenerated,
  } = useStateContext();

  const navigate = useNavigate();

  const handleTableClick = (tableName) => {
    setSelectedTable(tableName);
    navigate(`/table/${tableName}`);
  };

  const messagePanelStyle = {
    backgroundColor: '#1f2937', // your dark theme base (e.g., Tailwind's gray-800)
    color: '#e5e7eb',           // light gray text (e.g., gray-200)
    padding: '16px',
    height: '100%',
    overflowY: 'auto',
    fontFamily: 'Segoe UI, sans-serif',
  };

  const messageStyle = {
    backgroundColor: '#374151', // slightly lighter than panel (e.g., gray-700)
    padding: '10px 14px',
    borderRadius: '8px',
    marginBottom: '10px',
    fontSize: '0.95rem',
    lineHeight: '1.4',
    whiteSpace: 'pre-wrap',
  };

  const dropdownContainerStyle = {
    marginTop: '20px',
    marginBottom: '10px',
  };

  const dropdownStyle = {
    width: '100%',
    padding: '10px',
    backgroundColor: '#111827', // darkest background (gray-900)
    color: '#e5e7eb',
    border: '1px solid #4b5563', // border gray-600
    borderRadius: '6px',
    fontSize: '0.95rem',
  };

  const iconStyle = {
    cursor: 'pointer',
    marginRight: '14px',
    fontSize: '1.2rem',
    color: '#93c5fd', // sky-300 or any accent
  };

  return (
    <div style={messagePanelStyle}>
      {/* Icons */}
      <div style={{ marginBottom: '12px', display: 'flex', alignItems: 'center' }}>
        {erDiagramGenerated && (
          <FaSitemap title="ER Diagram" onClick={() => navigate('/erd')} style={iconStyle} />
        )}
        {fdGenerated && (
          <PiFunctionLight title="Functional Dependencies" onClick={() => navigate('/fds')} style={iconStyle} />
        )}
      </div>

      {/* Messages */}
      {messages.map((msg, idx) => (
        <div key={idx} style={messageStyle}>
          {msg}
        </div>
      ))}

      {/* Normalized Table Dropdown */}
      {normalizedTables.length > 0 && (
        <div style={dropdownContainerStyle}>
          <select
            style={dropdownStyle}
            onChange={(e) => handleTableClick(e.target.value)}
            defaultValue=""
          >
            <option value="" disabled>Select a normalized table</option>
            {normalizedTables.map((table, index) => (
              <option key={index} value={table}>{table}</option>
            ))}
          </select>
        </div>
      )}
    </div>
  );
};

export default MessagePanel;
