import React from 'react';
import { useNavigate } from 'react-router-dom';
import { useStateContext } from '../context/StateContext';

const MessagePanel = () => {
  const { messages, normalizedTables, loadNormalizedTables, isLoading } = useStateContext();
  const navigate = useNavigate();

  const handleTableClick = (tableName) => {
    navigate(`/table/${tableName}`);
  };

  return (
    <div className="message-panel">
      <h2>Messages</h2>
      <div className="messages-area" style={{ height: '150px', overflowY: 'auto', border: '1px solid #ddd', padding: '10px' }}>
        {messages.map((msg, index) => (
          <div key={index}>{msg}</div>
        ))}
      </div>

      <div style={{ marginTop: '20px' }}>
        <button onClick={loadNormalizedTables} style={{ padding: '6px 12px', marginBottom: '10px' }}>
          ðŸ“‹ Load Normalized Tables
        </button>

        <div className="table-buttons" style={{ display: 'flex', flexDirection: 'column', gap: '8px' }}>
          {normalizedTables.map((tableName, index) => (
            <button
              key={index}
              onClick={() => handleTableClick(tableName)}
              style={{
                padding: '6px 12px',
                backgroundColor: '#f5f5f5',
                border: '1px solid #ccc',
                borderRadius: '6px',
                cursor: 'pointer'
              }}
            >
              {tableName}
            </button>
          ))}
        </div>
      </div>

      {isLoading && <p>Loading...</p>}
    </div>
  );
};

export default MessagePanel;
