// src/components/ActionPanel.jsx or .tsx

import React, { useRef } from 'react';
import {
  Upload,
  FileSpreadsheet,
  Trash2,
  Search,
  Key,
  Database,
  Shield,
  CheckCircle,
  GitBranch,
  RotateCcw,
} from 'lucide-react';

import {
  uploadFile,
  triggerBackendStep,
  triggerLosslessCheck,
} from '../api';
import { useStateContext } from '../context/StateContext';

const actions = [
  { id: 'upload-file', label: 'Upload File', icon: Upload, color: 'bg-blue-500' },
  { id: 'convert-csv', label: 'Convert to CSV', icon: FileSpreadsheet, color: 'bg-green-500' },
  { id: 'clean', label: 'Clean Data', icon: Trash2, color: 'bg-yellow-500' },
  { id: 'fd-detection', label: 'FD Detection', icon: Search, color: 'bg-purple-500' },
  { id: 'key-detection', label: 'Key Detection', icon: Key, color: 'bg-indigo-500' },
  { id: 'normalized-tables', label: 'Normalized Tables', icon: Database, color: 'bg-teal-500' },
  { id: 'dependency-preservation', label: 'Dependency Preservation', icon: Shield, color: 'bg-orange-500' },
  { id: 'lossless-check', label: 'Lossless Check', icon: CheckCircle, color: 'bg-emerald-500' },
  { id: 'er-diagram', label: 'ER Diagram Generator', icon: GitBranch, color: 'bg-pink-500' },
  { id: 'refresh-workflow', label: 'Refresh Workflow', icon: RotateCcw, color: 'bg-red-500' },
];

const ActionPanel = () => {
  const {
    addMessage,
    addWorkflowStep,
    fetchNormalizedTables,
    originalFDs,
    decomposedSchemas,
    fetchOriginalFDs,
    fetchDecomposedSchemas,
    checkDependencyPreservation,
    setErDiagramGenerated,
    setFDGenerated,
  } = useStateContext();

  const fileInputRef = useRef(null);

  const handleFileUpload = async (event) => {
    const file = event.target.files[0];
    if (!file) return;

    try {
      const message = await uploadFile(file);
      addMessage(message);
      addWorkflowStep('Upload');
    } catch (error) {
      addMessage(error.message);
    }
  };

  const handleBackendAction = async (stepName) => {
    try {
      const message = await triggerBackendStep(stepName);
      addMessage(message);
      addWorkflowStep(stepName);

      if (stepName === 'FDModified') {
        await fetchOriginalFDs();
        setFDGenerated(true);
        console.log('FDGenerated set to true');

        await fetch('/api/detected_fds')
          .then((res) => res.json())
          .then((data) => {
            fetchOriginalFDs(data.fds || []);
            setFDGenerated(true);
          });
      }

      if (stepName === 'NormalizeTable') {
        await fetchDecomposedSchemas();
        await fetchNormalizedTables();
        addMessage('Tables normalized successfully');
      }

      if (stepName === 'ERDiagram') {
        setErDiagramGenerated(true);
      }

    } catch (error) {
      addMessage(error.message);
    }
  };

  const handleDependencyPreservation = async () => {
    try {
      await fetchOriginalFDs();
      await fetchDecomposedSchemas();

      if (!originalFDs || originalFDs.length === 0) {
        addMessage('Error: Functional Dependencies are missing or invalid.');
        return;
      }

      if (!decomposedSchemas || decomposedSchemas.length === 0) {
        addMessage('Error: Decomposed Schemas are missing or invalid.');
        return;
      }

      const message = await checkDependencyPreservation(originalFDs, decomposedSchemas);
      addMessage(message);
    } catch (error) {
      addMessage(error.message || 'Unknown error occurred during dependency preservation check.');
    }
  };

  const handleLosslessCheck = async () => {
    try {
      const message = await triggerLosslessCheck();
      addMessage(message);
      addWorkflowStep('LosslessCheck');
    } catch (error) {
      addMessage(error.message || 'Unknown error during Lossless Check');
    }
  };

  const handleActionClick = (id) => {
    switch (id) {
      case 'upload-file':
        fileInputRef.current.click();
        break;
      case 'convert-csv':
        handleBackendAction('ConvertToCSV');
        break;
      case 'clean':
        handleBackendAction('CleanModify');
        break;
      case 'fd-detection':
        handleBackendAction('FDModified');
        break;
      case 'key-detection':
        handleBackendAction('KeyDetection');
        break;
      case 'normalized-tables':
        handleBackendAction('NormalizeTable');
        break;
      case 'dependency-preservation':
        handleDependencyPreservation();
        break;
      case 'lossless-check':
        handleLosslessCheck();
        break;
      case 'er-diagram':
        handleBackendAction('ERDiagram');
        break;
      case 'refresh-workflow':
        window.location.reload();
        break;
      default:
        break;
    }
  };

  return (
    <div className="p-4">
      <h2 className="text-lg font-semibold text-gray-800 mb-4">Actions</h2>

      {/* Hidden file input for Upload */}
      <input
        type="file"
        ref={fileInputRef}
        onChange={handleFileUpload}
        style={{ display: 'none' }}
      />

      <div className="space-y-5">
        {actions.map((action) => {
          const Icon = action.icon;
          return (
            <button
              key={action.id}
              onClick={() => handleActionClick(action.id)}
              className="w-full flex items-center space-x-4 p-3 bg-gray-50 hover:bg-gray-100 rounded-lg transition-colors duration-200"
              style={{ textAlign: 'left' }}
            >
              <div
                className={`p-2 rounded-md ${action.color} text-white transition-transform duration-200 group-hover:scale-110`}
                aria-hidden="true"
              >
                <Icon size={18} />
              </div>
              <span className="text-sm font-medium text-gray-700">
                {action.label}
              </span>
            </button>
          );
        })}
      </div>
    </div>
  );
};

export default ActionPanel;
