import React, { useEffect, useState, useRef } from 'react';
import { useStateContext } from '../context/StateContext';
import DraggableStep from './DraggableStep';
import axios from 'axios';

const OutputPanel = () => {
  const { workflowSteps, setCurrentCode } = useStateContext();
  const [positions, setPositions] = useState({});
  const [scale, setScale] = useState(1); // zoom scale
  const containerRef = useRef(null);

  useEffect(() => {
    const initialPositions = {};
    workflowSteps.forEach((step, index) => {
      initialPositions[step] = { x: 50, y: 50 + index * 80 };
    });
    setPositions((prev) => ({ ...prev, ...initialPositions }));
  }, [workflowSteps]);

  const handlePositionChange = (step, pos) => {
    setPositions((prev) => ({ ...prev, [step]: pos }));
  };

  const handleClickStep = async (step) => {
    try {
      const response = await axios.get(`/api/code/${step}`);
      setCurrentCode(response.data.code || '');
    } catch {
      setCurrentCode('Failed to load code');
    }
  };

  const renderLines = () => {
    const lines = [];
    for (let i = 0; i < workflowSteps.length - 1; i++) {
      const from = positions[workflowSteps[i]];
      const to = positions[workflowSteps[i + 1]];
      if (from && to) {
        // Assume DraggableStep approx size: width=120px, height=50px
        // Start point at right middle of from box
        const startX = from.x + 120;
        const startY = from.y + 25;
        // End point at left middle of to box
        const endX = to.x;
        const endY = to.y + 25;

        // Calculate control points for a smooth horizontal curve
        const deltaX = endX - startX;
        const controlX1 = startX + deltaX / 2;
        const controlY1 = startY;
        const controlX2 = startX + deltaX / 2;
        const controlY2 = endY;

        const d = `M${startX},${startY} C${controlX1},${controlY1} ${controlX2},${controlY2} ${endX},${endY}`;

        lines.push(
          <path
            key={`line-${i}`}
            d={d}
            fill="none"
            stroke="rgba(255,255,255,0.9)"
            strokeWidth="2"
            markerEnd="url(#arrowhead)"
          />
        );
      }
    }
    return lines;
  };

  const zoomIn = () => setScale((s) => Math.min(2, +(s + 0.1).toFixed(2)));
  const zoomOut = () => setScale((s) => Math.max(0.5, +(s - 0.1).toFixed(2)));

  return (
    <div
      className="output-panel"
      style={{
        width: '100%',
        height: '100%',
        position: 'relative',
        overflow: 'hidden',
      }}
      ref={containerRef}
    >
      {/* Zoom controls */}
      <div className="zoom-controls">
        <button className="zoom-btn" onClick={zoomIn} title="Zoom in">
          +
        </button>
        <button className="zoom-btn" onClick={zoomOut} title="Zoom out">
          −
        </button>
      </div>

      {/* Canvas wrapper */}
      <div
        className="canvas-wrapper"
        style={{
          transform: `scale(${scale})`,
          transformOrigin: '0 0',
          width: '100%',
          height: '100%',
          position: 'absolute',
          left: 0,
          top: 0,
        }}
      >
        {/* Lines with arrowheads */}
        <svg
          style={{
            position: 'absolute',
            width: '100%',
            height: '100%',
            pointerEvents: 'none',
            overflow: 'visible',
          }}
        >
          <defs>
            <marker
              id="arrowhead"
              markerWidth="10"
              markerHeight="7"
              refX="10"
              refY="3.5"
              orient="auto"
              fill="rgba(255,255,255,0.9)"
            >
              <polygon points="0 0, 10 3.5, 0 7" />
            </marker>
          </defs>
          {renderLines()}
        </svg>

        {/* Draggable steps */}
        {workflowSteps.map((step) => (
          <DraggableStep
            key={step}
            step={step}
            position={positions[step] || { x: 0, y: 0 }}
            onPositionChange={handlePositionChange}
            onClick={handleClickStep}
          />
        ))}
      </div>
    </div>
  );
};

export default OutputPanel;
