import React from 'react';
import { useDrag } from '@use-gesture/react';

const DraggableStep = ({ step, position, onPositionChange, onClick }) => {
  const bind = useDrag(({ offset: [x, y] }) => {
    onPositionChange(step, { x, y });
  });

  return (
    <div
      {...bind()}
      onClick={() => onClick(step)}
      style={{
        position: 'absolute',
        left: position.x,
        top: position.y,
        padding: '10px 20px',
        backgroundColor: '#f0f0f0',
        border: '1px solid #ccc',
        borderRadius: '8px',
        cursor: 'pointer',
        userSelect: 'none',
        touchAction: 'none', // Prevent touch scrolling interference
      }}
    >
      {step}
    </div>
  );
};

export default DraggableStep;
