import React, { useEffect, useState, useRef } from 'react';
import { useStateContext } from '../context/StateContext';
import DraggableStep from './DraggableStep';
import axios from 'axios';

const STORAGE_KEY = 'workflowStepPositions';

const OutputPanel = () => {
  const { workflowSteps, setCurrentCode } = useStateContext();
  const [positions, setPositions] = useState({});
  const [scale, setScale] = useState(1);
  const containerRef = useRef(null);

  // Store control points for each line in state, keyed by line index
  // Each value: { c1: {x, y}, c2: {x, y} }
  const [controlPoints, setControlPoints] = useState({});

  const positionsInitialized = useRef(false);

  const boxWidth = 140;
  const boxHeight = 44;
  const horizontalSpacing = 60;
  const verticalSpacing = 60;

  // Load saved positions from localStorage on mount
  useEffect(() => {
    const savedPositions = localStorage.getItem(STORAGE_KEY);
    if (savedPositions) {
      try {
        const parsedPositions = JSON.parse(savedPositions);
        setPositions(parsedPositions);
        positionsInitialized.current = true;
      } catch {
        console.warn('Failed to parse saved positions');
      }
    }
  }, []);

  // Initialize default positions if none saved
  useEffect(() => {
    if (positionsInitialized.current) return;
    if (!containerRef.current) return;

    const containerWidth = containerRef.current.clientWidth;
    const initialPositions = {};

    let x = 50;
    let y = 60;

    workflowSteps.forEach((step) => {
      if (x + boxWidth > containerWidth - 20) {
        x = 50;
        y += boxHeight + verticalSpacing;
      }
      initialPositions[step] = { x, y };
      x += boxWidth + horizontalSpacing;
    });

    setPositions((prev) => ({ ...prev, ...initialPositions }));
    positionsInitialized.current = true;
  }, [workflowSteps]);

  // Save positions
  useEffect(() => {
    if (positionsInitialized.current) {
      localStorage.setItem(STORAGE_KEY, JSON.stringify(positions));
    }
  }, [positions]);

  // Handle drag of step boxes
  const handlePositionChange = (step, pos) => {
    setPositions((prev) => ({ ...prev, [step]: pos }));
  };

  const handleClickStep = async (step) => {
    try {
      const response = await axios.get(`/api/code/${step}`);
      setCurrentCode(response.data.code || '');
    } catch {
      setCurrentCode('Failed to load code');
    }
  };

  // Handle drag for control point dots
  // lineIndex: which line, pointKey: "c1" or "c2"
  const handleControlPointDrag = (lineIndex, pointKey, e) => {
    e.preventDefault();
    e.stopPropagation();

    const containerRect = containerRef.current.getBoundingClientRect();
    const mouseX = (e.clientX - containerRect.left) / scale;
    const mouseY = (e.clientY - containerRect.top) / scale;

    setControlPoints((prev) => ({
      ...prev,
      [lineIndex]: {
        ...prev[lineIndex],
        [pointKey]: { x: mouseX, y: mouseY },
      },
    }));
  };

  // To implement drag, we need to set up mousemove and mouseup listeners when dragging starts.
  // We'll do a custom hook below.

  // Drag state
  const draggingRef = useRef(null);

  const onMouseDownControlPoint = (lineIndex, pointKey, e) => {
    e.preventDefault();
    e.stopPropagation();
    draggingRef.current = { lineIndex, pointKey };

    window.addEventListener('mousemove', onMouseMoveControlPoint);
    window.addEventListener('mouseup', onMouseUpControlPoint);
  };

  const onMouseMoveControlPoint = (e) => {
    if (!draggingRef.current) return;

    const { lineIndex, pointKey } = draggingRef.current;
    const containerRect = containerRef.current.getBoundingClientRect();
    const mouseX = (e.clientX - containerRect.left) / scale;
    const mouseY = (e.clientY - containerRect.top) / scale;

    setControlPoints((prev) => ({
      ...prev,
      [lineIndex]: {
        ...prev[lineIndex],
        [pointKey]: { x: mouseX, y: mouseY },
      },
    }));
  };

  const onMouseUpControlPoint = (e) => {
    draggingRef.current = null;
    window.removeEventListener('mousemove', onMouseMoveControlPoint);
    window.removeEventListener('mouseup', onMouseUpControlPoint);
  };

  // Render the BÃ©zier lines and control points
  const renderLines = () => {
    const dotRadius = 6;
    const lines = [];
    const containerWidth = containerRef.current
      ? containerRef.current.clientWidth
      : 1000;

    for (let i = 0; i < workflowSteps.length - 1; i++) {
      const from = positions[workflowSteps[i]];
      const to = positions[workflowSteps[i + 1]];

      if (from && to) {
        const fromNearRightEdge = from.x + boxWidth > containerWidth - 45;
        const toNearRightEdge = to.x + boxWidth > containerWidth - 45;

        const startX = fromNearRightEdge ? from.x - dotRadius : from.x + boxWidth + dotRadius;
        const startY = from.y + boxHeight / 2;

        const endX = toNearRightEdge ? to.x + boxWidth + dotRadius : to.x - dotRadius;
        const endY = to.y + boxHeight / 2;

        // Use stored control points if available, else calculate default
        const cp = controlPoints[i] || {};

        let controlX1, controlY1, controlX2, controlY2;

        if (cp.c1 && cp.c2) {
          controlX1 = cp.c1.x;
          controlY1 = cp.c1.y;
          controlX2 = cp.c2.x;
          controlY2 = cp.c2.y;
        } else {
          if (Math.abs(endX - startX) > Math.abs(endY - startY)) {
            controlX1 = startX + (endX - startX) / 2;
            controlY1 = startY;
            controlX2 = startX + (endX - startX) / 2;
            controlY2 = endY;
          } else {
            controlX1 = startX;
            controlY1 = startY + (endY - startY) / 2;
            controlX2 = endX;
            controlY2 = startY + (endY - startY) / 2;
          }
        }

        const d = `M${startX},${startY} C${controlX1},${controlY1} ${controlX2},${controlY2} ${endX},${endY}`;

        lines.push(
          <path
            key={`line-${i}`}
            d={d}
            fill="none"
            stroke="rgba(184, 212, 249, 0.9)"
            strokeWidth="1.5"
            markerEnd="url(#arrowhead)"
          />
        );

        // Render draggable control dots
        lines.push(
          <circle
            key={`cp1-${i}`}
            cx={controlX1}
            cy={controlY1}
            r={dotRadius}
            fill="rgba(70, 130, 180, 0.8)"
            stroke="#333"
            strokeWidth="1.5"
            style={{ cursor: 'grab' }}
            onMouseDown={(e) => onMouseDownControlPoint(i, 'c1', e)}
          />,
          <circle
            key={`cp2-${i}`}
            cx={controlX2}
            cy={controlY2}
            r={dotRadius}
            fill="rgba(70, 130, 180, 0.8)"
            stroke="#333"
            strokeWidth="1.5"
            style={{ cursor: 'grab' }}
            onMouseDown={(e) => onMouseDownControlPoint(i, 'c2', e)}
          />
        );
      }
    }
    return lines;
  };

  const zoomIn = () => setScale((s) => Math.min(2, +(s + 0.1).toFixed(2)));
  const zoomOut = () => setScale((s) => Math.max(0.5, +(s - 0.1).toFixed(2)));

  return (
    <div
      className="output-panel"
      style={{
        width: '100%',
        height: '100%',
        position: 'relative',
        overflow: 'auto',
        padding: 20,
      }}
      ref={containerRef}
    >
      {/* Zoom controls */}
      <div
        className="zoom-controls"
        style={{ position: 'absolute', top: 5, right: 5, zIndex: 5 }}
      >
        <button
          className="zoom-btn"
          onClick={zoomIn}
          title="Zoom in"
          style={{ marginRight: 5, textAlign: 'center' }}
        >
          +
        </button>
        <button className="zoom-btn" onClick={zoomOut} title="Zoom out">
          â
        </button>
      </div>

      {/* Canvas wrapper */}
      <div
        className="canvas-wrapper"
        style={{
          transform: `scale(${scale})`,
          transformOrigin: '0 0',
          position: 'relative',
          minHeight: '100%',
          width: '100%',
        }}
      >
        {/* SVG lines and control dots */}
        <svg
          style={{
            position: 'absolute',
            width: '100%',
            height: '100%',
            pointerEvents: 'none', // we will override pointerEvents on circles
            overflow: 'visible',
            top: 0,
            left: 0,
          }}
        >
          <defs>
            <marker
              id="arrowhead"
              markerWidth="10"
              markerHeight="7"
              refX="10"
              refY="3.5"
              orient="auto"
              fill="rgba(103, 121, 136, 0.9)"
            >
              <polygon points="0 0, 10 3.5, 0 7" />
            </marker>
          </defs>
          {renderLines()}
        </svg>

        {/* Draggable steps */}
        {workflowSteps.map((step, index) => (
          <DraggableStep
            key={step}
            step={step}
            position={positions[step] || { x: 0, y: 0 }}
            onPositionChange={handlePositionChange}
            onClick={handleClickStep}
            isFirst={index === 0}
          />
        ))}
      </div>
    </div>
  );
};

export default OutputPanel;
