import React, { useEffect, useRef } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { StateProvider } from './context/StateContext';
import { Helmet } from 'react-helmet';
import {
  FaDatabase,
  FaTable,
  FaProjectDiagram,
  FaCode,
  FaCommentAlt,
  FaStream
} from 'react-icons/fa';

import ActionPanel from './components/ActionPanel';
import OutputPanel from './components/OutputPanel';
import CodePanel from './components/CodePanel';
import MessagePanel from './components/messagePanel';
import NormalizedTableView from './components/TableView';
import ERDiagramPage from './components/ERDiagramPage';
import FDPage from './components/FDPage';

import './style.css';
import '@fortawesome/fontawesome-free/css/all.min.css';

const PanelContainer = ({ children, title, icon }) => (
  <div className="panel-base">
    <div className="panel-header">
      <h3 className="panel-title">
        {icon && React.cloneElement(icon, { className: 'panel-icon' })}
        {title}
      </h3>
    </div>
    <div className="panel-content">{children}</div>
  </div>
);

const MainLayout = () => {
  const leftRef = useRef(null);
  const centerRef = useRef(null);
  const messagesRef = useRef(null);

  useEffect(() => {
    /** Helper for horizontal resizing */
    const initHorizontalResize = (handle, leftEl, rightEl) => {
      let startX, startWidth;

      handle.addEventListener('mousedown', e => {
        startX = e.clientX;
        startWidth = leftEl.offsetWidth;
        document.addEventListener('mousemove', onMouseMove);
        document.addEventListener('mouseup', onMouseUp);
      });

      const onMouseMove = e => {
        const newWidth = startWidth + (e.clientX - startX);
        leftEl.style.width = `${newWidth}px`;
      };

      const onMouseUp = () => {
        document.removeEventListener('mousemove', onMouseMove);
        document.removeEventListener('mouseup', onMouseUp);
      };
    };

    /** Helper for vertical resizing */
    const initVerticalResize = (handle, topEl) => {
      let startY, startHeight;

      handle.addEventListener('mousedown', e => {
        startY = e.clientY;
        startHeight = topEl.offsetHeight;
        document.addEventListener('mousemove', onMouseMove);
        document.addEventListener('mouseup', onMouseUp);
      });

      const onMouseMove = e => {
        const newHeight = startHeight + (e.clientY - startY);
        topEl.style.height = `${newHeight}px`;
      };

      const onMouseUp = () => {
        document.removeEventListener('mousemove', onMouseMove);
        document.removeEventListener('mouseup', onMouseUp);
      };
    };

    // Horizontal: left <-> center
    initHorizontalResize(
      document.querySelector('.resize-handle-x.left-center'),
      leftRef.current,
      centerRef.current
    );

    // Horizontal: center <-> right
    initHorizontalResize(
      document.querySelector('.resize-handle-x.center-right'),
      centerRef.current,
      document.querySelector('.right-panel')
    );

    // Vertical inside right panel
    initVerticalResize(
      document.querySelector('.resize-handle-y'),
      messagesRef.current
    );
  }, []);

  return (
    <div className="app-container">
      <div className="left-panel" ref={leftRef}>
        <PanelContainer title="Actions" icon={<FaDatabase />}>
          <ActionPanel />
        </PanelContainer>
      </div>

      <div className="resize-handle-x left-center"></div>

      <div className="center-panel" ref={centerRef}>
        <PanelContainer title="Database Output" icon={<FaTable />}>
          <OutputPanel />
        </PanelContainer>
      </div>

      <div className="resize-handle-x center-right"></div>

      <div className="right-panel">
        <div className="messages-panel" ref={messagesRef}>
          <PanelContainer title="Messages" icon={<FaCommentAlt />}>
            <MessagePanel />
          </PanelContainer>
        </div>

        <div className="resize-handle-y"></div>

        <div className="code-panel">
          <PanelContainer title="Generated Code" icon={<FaCode />}>
            <CodePanel />
          </PanelContainer>
        </div>
      </div>
    </div>
  );
};

const App = () => {
  return (
    <StateProvider>
      <Helmet>
        <title>Database Design Studio</title>
        <meta
          name="description"
          content="Tool for creating database designs and ER diagrams"
        />
        <link rel="preconnect" href="https://fonts.googleapis.com" />
        <link rel="preconnect" href="https://fonts.gstatic.com" crossOrigin="true" />
        <link
          href="https://fonts.googleapis.com/css2?family=Inter:wght@400;500;600;700&family=Fira+Code:wght@400;500&display=swap"
          rel="stylesheet"
        />
      </Helmet>

      <Router>
        <Routes>
          <Route path="/" element={<MainLayout />} />
          <Route
            path="/table-view"
            element={
              <PanelContainer title="Normalized Tables" icon={<FaTable />}>
                <NormalizedTableView />
              </PanelContainer>
            }
          />
          <Route
            path="/er-diagram"
            element={
              <PanelContainer title="ER Diagram" icon={<FaProjectDiagram />}>
                <ERDiagramPage />
              </PanelContainer>
            }
          />
          <Route
            path="/fd-view"
            element={
              <PanelContainer title="Detected Functional Dependencies" icon={<FaStream />}>
                <FDPage />
              </PanelContainer>
            }
          />
        </Routes>
      </Router>

      {/* SVG Arrowhead Definitions */}
      <svg className="svg-defs">
        <defs>
          <marker
            id="arrowhead"
            markerWidth="10"
            markerHeight="7"
            refX="9"
            refY="3.5"
            orient="auto"
          >
            <polygon points="0 0, 10 3.5, 0 7" fill="#1a73e8" />
          </marker>
        </defs>
      </svg>
    </StateProvider>
  );
};

export default App;
