import React, { useRef } from "react";
import axios from "axios";
import { useStateContext } from "./context/StateContext";

const ActionPanel = () => {
  const { addWorkflowStep, addMessage } = useStateContext();
  const fileInputRef = useRef(null);

  const handleFileUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;
    const formData = new FormData();
    formData.append("file", file);

    try {
      const res = await axios.post("/api/upload", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      addMessage(res.data.message || "File uploaded successfully");
      addWorkflowStep("Upload");
    } catch {
      addMessage("File upload failed");
    }
  };

  const handleAction = async (apiPath, stepName, successMsg, failMsg) => {
    try {
      const res = await axios.post(apiPath);
      addMessage(res.data.message || successMsg);
      addWorkflowStep(stepName);
    } catch {
      addMessage(failMsg);
    }
  };

  return (
    <div className="action-panel">
      <h2>Action Panel</h2>

      <input
        type="file"
        ref={fileInputRef}
        onChange={handleFileUpload}
        style={{ display: "none" }}
      />
      <button onClick={() => fileInputRef.current.click()}>Upload File</button>

      <div className="actions">
        <button
          onClick={() =>
            handleAction(
              "/api/convert_to_csv",
              "ConvertToCSV",
              "Converted to CSV",
              "Conversion failed"
            )
          }
        >
          Convert to CSV
        </button>

        <button
          onClick={() =>
            handleAction(
              "/api/clean_modify",
              "CleanModify",
              "Data cleaned",
              "Clean & modify failed"
            )
          }
        >
          Clean & Modify
        </button>

        <button
          onClick={() =>
            handleAction(
              "/api/fd_modified",
              "FDModified",
              "Functional dependencies detected",
              "FD detection failed"
            )
          }
        >
          Detect Functional Dependencies
        </button>

        <button
          onClick={() =>
            handleAction(
              "/api/normalize_table",
              "NormalizeTable",
              "Tables normalized",
              "Normalization failed"
            )
          }
        >
          Normalize Tables
        </button>

        <button
          onClick={() =>
            handleAction(
              "/api/key_detection",
              "KeyDetection",
              "Keys detected",
              "Key detection failed"
            )
          }
        >
          Detect Keys
        </button>

        <button
          onClick={() =>
            handleAction(
              "/api/dependency_preservation",
              "DependencyPreservation",
              "Dependency preservation checked",
              "Dependency preservation check failed"
            )
          }
        >
          Check Dependency Preservation
        </button>

        <button
          onClick={() =>
            handleAction(
              "/api/lossless_check",
              "LosslessCheck",
              "Lossless decomposition checked",
              "Lossless check failed"
            )
          }
        >
          Check Lossless Decomposition
        </button>

        <button
          onClick={() =>
            handleAction(
              "/api/generate_er_diagram",
              "ERDiagram",
              "ER Diagram generated",
              "ER Diagram generation failed"
            )
          }
        >
          Generate ER Diagram
        </button>
      </div>
    </div>
  );
};

export default ActionPanel;
