import React, { useEffect, useState, useRef } from 'react';
import axios from 'axios';

const ERDiagramPage = () => {
  const [imageURL, setImageURL] = useState(null);
  const [error, setError] = useState(null);
  const containerRef = useRef(null);
  const scaleRef = useRef(1);

  useEffect(() => {
    const container = containerRef.current;
    if (!container) return;

    const onWheel = (e) => {
      if (!e.ctrlKey) return;
      e.preventDefault();

      const delta = e.deltaY > 0 ? -0.1 : 0.1;
      scaleRef.current = Math.min(Math.max(0.5, scaleRef.current + delta), 3);

      container.style.transform = `scale(${scaleRef.current})`;
    };

    container.addEventListener('wheel', onWheel, { passive: false });

    return () => {
      container.removeEventListener('wheel', onWheel);
    };
  }, []);

  const resetZoom = () => {
    const container = containerRef.current;
    if (!container) return;

    scaleRef.current = 1;
    container.style.transform = 'scale(1)';
  };

  const goBack = () => {
    window.history.back();
  };

  useEffect(() => {
    const fetchERDiagram = async () => {
      try {
        const response = await axios.get('/api/get_er_diagram_image', { responseType: 'blob' });
        const blobUrl = URL.createObjectURL(response.data);
        setImageURL(blobUrl);
      } catch (err) {
        setError(err.response?.data?.message || 'Failed to load ER Diagram');
      }
    };

    fetchERDiagram();
  }, []);

  const smallButtonStyle = {
    padding: '6px 14px',
    fontSize: 14,
    cursor: 'pointer',
    borderRadius: 4,
    border: '1px solid #4b6cb7',
    backgroundColor: '#4b6cb7',
    color: 'white',
    transition: 'background-color 0.3s ease',
  };

  const onMouseEnter = (e) => (e.currentTarget.style.backgroundColor = '#39519a');
  const onMouseLeave = (e) => (e.currentTarget.style.backgroundColor = '#4b6cb7');

  return (
    <div
      style={{
        maxWidth: 900,
        margin: '0 auto',
        padding: '20px',
        fontFamily: "'Roboto', sans-serif",
      }}
    >
      <h2
        style={{
          textAlign: 'center',
          fontSize: '2.5rem',
          fontWeight: '700',
          color: '#182848',
          marginBottom: '12px',
          userSelect: 'none',
          letterSpacing: '1.2px',
        }}
      >
        ER Diagram
      </h2>

      {/* Buttons container below header */}
      <div
        style={{
          display: 'flex',
          justifyContent: 'center',
          gap: '16px',
          marginBottom: '20px',
        }}
      >
        <button
          onClick={resetZoom}
          style={smallButtonStyle}
          onMouseEnter={onMouseEnter}
          onMouseLeave={onMouseLeave}
          aria-label="Reset Zoom"
          type="button"
        >
          Reset Zoom
        </button>
        <button
          onClick={goBack}
          style={smallButtonStyle}
          onMouseEnter={onMouseEnter}
          onMouseLeave={onMouseLeave}
          aria-label="Go Back"
          type="button"
        >
          Back
        </button>
      </div>

      {error && (
        <div style={{ color: 'red', marginBottom: 16, textAlign: 'center' }}>
          {error}
        </div>
      )}

      <div
        ref={containerRef}
        style={{
          border: '1px solid #ccc',
          overflow: 'auto',
          width: '100%',
          height: '77vh',
          cursor: 'grab',
          transformOrigin: '0 0',
        }}
      >
        {imageURL && (
          <img
            src={imageURL}
            alt="ER Diagram"
            style={{ width: '100%', height: 'auto', display: 'block' }}
            draggable={false}
          />
        )}
      </div>

      <p
        style={{
          fontSize: '0.9rem',
          color: '#666',
          marginTop: 12,
          textAlign: 'center',
        }}
      >
        Use <strong>Ctrl + Mouse Wheel</strong> to zoom. Drag to pan.
      </p>
    </div>
  );
};

export default ERDiagramPage;
