import React, { useEffect, useState, useRef } from 'react';
import { useStateContext } from '../context/StateContext';
import DraggableStep from './DraggableStep';
import axios from 'axios';

const STORAGE_KEY = 'workflowStepPositions';

const OutputPanel = () => {
  const { workflowSteps, setCurrentCode } = useStateContext();
  const [positions, setPositions] = useState({});
  const [controlPoints, setControlPoints] = useState({});
  const [scale, setScale] = useState(1);
  const containerRef = useRef(null);

  // Undo/Redo history stacks and pointer
  const historyRef = useRef({
    stack: [],
    pointer: -1,
  });

  const positionsInitialized = useRef(false);

  const boxWidth = 140;
  const boxHeight = 44;
  const horizontalSpacing = 60;
  const verticalSpacing = 60;

  // Save current state to history
  const saveHistory = (newPositions, newControlPoints) => {
    const { stack, pointer } = historyRef.current;
    const newEntry = {
      positions: newPositions,
      controlPoints: newControlPoints,
    };

    // If we are not at the end of stack, discard redo history
    const newStack = stack.slice(0, pointer + 1);
    newStack.push(newEntry);

    // Limit history length if desired (optional)
    const MAX_HISTORY = 50;
    if (newStack.length > MAX_HISTORY) {
      newStack.shift(); // remove oldest
    }

    const newPointer = Math.min(newStack.length - 1, pointer + 1);

    historyRef.current = {
      stack: newStack,
      pointer: newPointer,
    };
  };

  // Load saved positions from localStorage on mount
  useEffect(() => {
    const savedPositions = localStorage.getItem(STORAGE_KEY);
    if (savedPositions) {
      try {
        const parsedPositions = JSON.parse(savedPositions);
        setPositions(parsedPositions);
        positionsInitialized.current = true;

        // Save initial state to history
        saveHistory(parsedPositions, {});
      } catch {
        console.warn('Failed to parse saved positions');
      }
    }
  }, []);

  // Initialize default positions if none saved
  useEffect(() => {
    if (positionsInitialized.current) return;
    if (!containerRef.current) return;

    const containerWidth = containerRef.current.clientWidth;
    const initialPositions = {};

    let x = 50;
    let y = 60;

    workflowSteps.forEach((step) => {
      if (x + boxWidth > containerWidth - 20) {
        x = 50;
        y += boxHeight + verticalSpacing;
      }
      initialPositions[step] = { x, y };
      x += boxWidth + horizontalSpacing;
    });

    setPositions((prev) => {
      const updated = { ...prev, ...initialPositions };
      saveHistory(updated, controlPoints);
      return updated;
    });
    positionsInitialized.current = true;
  }, [workflowSteps]);

  // Save positions to localStorage whenever positions change
  useEffect(() => {
    if (positionsInitialized.current) {
      localStorage.setItem(STORAGE_KEY, JSON.stringify(positions));
    }
  }, [positions]);

  // Save history when either positions or controlPoints change
  useEffect(() => {
    if (positionsInitialized.current) {
      saveHistory(positions, controlPoints);
    }
  }, [positions, controlPoints]);

  // Undo action
  const undo = () => {
    const { stack, pointer } = historyRef.current;
    if (pointer > 0) {
      const prev = stack[pointer - 1];
      historyRef.current.pointer = pointer - 1;
      setPositions(prev.positions);
      setControlPoints(prev.controlPoints);
    }
  };

  // Redo action
  const redo = () => {
    const { stack, pointer } = historyRef.current;
    if (pointer < stack.length - 1) {
      const next = stack[pointer + 1];
      historyRef.current.pointer = pointer + 1;
      setPositions(next.positions);
      setControlPoints(next.controlPoints);
    }
  };

  const handlePositionChange = (step, pos) => {
    setPositions((prev) => ({ ...prev, [step]: pos }));
  };

  const handleClickStep = async (step) => {
    try {
      const response = await axios.get(`/api/code/${step}`);
      setCurrentCode(response.data.code || '');
    } catch {
      setCurrentCode('Failed to load code');
    }
  };

  // Drag state for control points
  const draggingRef = useRef(null);

  const onMouseDownControlPoint = (lineIndex, pointKey, e) => {
    e.preventDefault();
    e.stopPropagation();
    draggingRef.current = { lineIndex, pointKey };

    window.addEventListener('mousemove', onMouseMoveControlPoint);
    window.addEventListener('mouseup', onMouseUpControlPoint);
  };

  const onMouseMoveControlPoint = (e) => {
    if (!draggingRef.current) return;

    const { lineIndex, pointKey } = draggingRef.current;
    const containerRect = containerRef.current.getBoundingClientRect();
    const mouseX = (e.clientX - containerRect.left) / scale;
    const mouseY = (e.clientY - containerRect.top) / scale;

    setControlPoints((prev) => ({
      ...prev,
      [lineIndex]: {
        ...prev[lineIndex],
        [pointKey]: { x: mouseX, y: mouseY },
      },
    }));
  };

  const onMouseUpControlPoint = () => {
    draggingRef.current = null;
    window.removeEventListener('mousemove', onMouseMoveControlPoint);
    window.removeEventListener('mouseup', onMouseUpControlPoint);
  };

  // Helper cubic Bézier function for point at parameter t
  function cubicBezierPoint(t, P0, P1, P2, P3) {
    const u = 1 - t;
    const tt = t * t;
    const uu = u * u;
    const uuu = uu * u;
    const ttt = tt * t;

    const x =
      uuu * P0.x +
      3 * uu * t * P1.x +
      3 * u * tt * P2.x +
      ttt * P3.x;
    const y =
      uuu * P0.y +
      3 * uu * t * P1.y +
      3 * u * tt * P2.y +
      ttt * P3.y;

    return { x, y };
  }

  // Render Bézier lines and draggable dots on curve
  const renderLines = () => {
    const dotRadius = 6;
    const lines = [];
    const containerWidth = containerRef.current
      ? containerRef.current.clientWidth
      : 1000;

    const t1 = 0.25; // parameter for first dot on curve
    const t2 = 0.75; // parameter for second dot on curve

    for (let i = 0; i < workflowSteps.length - 1; i++) {
      const from = positions[workflowSteps[i]];
      const to = positions[workflowSteps[i + 1]];

      if (from && to) {
        const fromNearRightEdge = from.x + boxWidth > containerWidth - 45;
        const toNearRightEdge = to.x + boxWidth > containerWidth - 45;

        const P0 = {
          x: fromNearRightEdge ? from.x - dotRadius : from.x + boxWidth + dotRadius,
          y: from.y + boxHeight / 2,
        };
        const P3 = {
          x: toNearRightEdge ? to.x + boxWidth + dotRadius : to.x - dotRadius,
          y: to.y + boxHeight / 2,
        };

        const cp = controlPoints[i] || {};

        let P1, P2;

        if (cp.c1 && cp.c2) {
          P1 = cp.c1;
          P2 = cp.c2;
        } else {
          if (Math.abs(P3.x - P0.x) > Math.abs(P3.y - P0.y)) {
            P1 = { x: P0.x + (P3.x - P0.x) / 2, y: P0.y };
            P2 = { x: P0.x + (P3.x - P0.x) / 2, y: P3.y };
          } else {
            P1 = { x: P0.x, y: P0.y + (P3.y - P0.y) / 2 };
            P2 = { x: P3.x, y: P0.y + (P3.y - P0.y) / 2 };
          }
        }

        // Compute dot positions on the curve
        const dot1Pos = cubicBezierPoint(t1, P0, P1, P2, P3);
        const dot2Pos = cubicBezierPoint(t2, P0, P1, P2, P3);

        // Build path string
        const d = `M${P0.x},${P0.y} C${P1.x},${P1.y} ${P2.x},${P2.y} ${P3.x},${P3.y}`;

        lines.push(
          <path
            key={`line-${i}`}
            d={d}
            fill="none"
            stroke="rgba(184, 212, 249, 0.9)"
            strokeWidth="1.5"
            markerEnd="url(#arrowhead)"
          />
        );

        // Draggable dots placed on curve at dot1Pos and dot2Pos
        lines.push(
          <circle
            key={`dot1-${i}`}
            cx={dot1Pos.x}
            cy={dot1Pos.y}
            r={dotRadius}
            fill="rgba(70, 130, 180, 0.9)"
            stroke="#333"
            strokeWidth="1.5"
            style={{ cursor: 'grab' }}
            pointerEvents="all"
            onMouseDown={(e) => onMouseDownControlPoint(i, 'c1', e)}
          />,
          <circle
            key={`dot2-${i}`}
            cx={dot2Pos.x}
            cy={dot2Pos.y}
            r={dotRadius}
            fill="rgba(70, 130, 180, 0.9)"
            stroke="#333"
            strokeWidth="1.5"
            style={{ cursor: 'grab' }}
            pointerEvents="all"
            onMouseDown={(e) => onMouseDownControlPoint(i, 'c2', e)}
          />
        );
      }
    }
    return lines;
  };

  const zoomIn = () => setScale((s) => Math.min(2, +(s + 0.1).toFixed(2)));
  const zoomOut = () => setScale((s) => Math.max(0.5, +(s - 0.1).toFixed(2)));

  // Enable or disable undo/redo buttons
  const canUndo = historyRef.current.pointer > 0;
  const canRedo =
    historyRef.current.pointer < historyRef.current.stack.length - 1;

  return (
    <div
      className="output-panel"
      style={{
        width: '100%',
        height: '100%',
        position: 'relative',
        overflow: 'auto',
        padding: 20,
      }}
      ref={containerRef}
    >
      {/* Controls */}
      <div
        className="zoom-controls"
        style={{ position: 'absolute', top: 5, right: 5, zIndex: 5 }}
      >
        <button
          className="undo-btn"
          onClick={undo}
          title="Undo"
          disabled={!canUndo}
          style={{ marginRight: 5, textAlign: 'center' }}
        >
          ⎌
        </button>
        <button
          className="redo-btn"
          onClick={redo}
          title="Redo"
          disabled={!canRedo}
          style={{ marginRight: 10, textAlign: 'center' }}
        >
          ↻
        </button>
        <button
          className="zoom-btn"
          onClick={zoomIn}
          title="Zoom in"
          style={{ marginRight: 5, textAlign: 'center' }}
        >
          +
        </button>
        <button className="zoom-btn" onClick={zoomOut} title="Zoom out">
          −
        </button>
      </div>

      {/* Canvas wrapper */}
      <div
        className="canvas-wrapper"
        style={{
          transform: `scale(${scale})`,
          transformOrigin: '0 0',
          position: 'relative',
          minHeight: '100%',
          width: '100%',
        }}
      >
        {/* SVG lines and draggable dots */}
        <svg
          style={{
            position: 'absolute',
            width: '100%',
            height: '100%',
            pointerEvents: 'none',
            overflow: 'visible',
            top: 0,
            left: 0,
          }}
        >
          <defs>
            <marker
              id="arrowhead"
              markerWidth="10"
              markerHeight="7"
              refX="10"
              refY="3.5"
              orient="auto"
              fill="rgba(103, 121, 136, 0.9)"
            >
              <polygon points="0 0, 10 3.5, 0 7" />
            </marker>
          </defs>
          {renderLines()}
        </svg>

        {/* Draggable steps */}
        {workflowSteps.map((step, index) => (
          <DraggableStep
            key={step}
            step={step}
            position={positions[step] || { x: 0, y: 0 }}
            onPositionChange={handlePositionChange}
            onClick={handleClickStep}
            isFirst={index === 0}
          />
        ))}
      </div>
    </div>
  );
};

export default OutputPanel;
