import React, { useEffect, useRef, useState } from "react";
import { useStateContext } from "../context/StateContext";
import Prism from "prismjs";
import "prismjs/themes/prism-tomorrow.css";

// Import the languages you want to support
import "prismjs/components/prism-javascript.min.js";
import "prismjs/components/prism-jsx.min.js";
import "prismjs/components/prism-markup.min.js"; // Correct for HTML
import "prismjs/components/prism-css.min.js";
import "prismjs/components/prism-python.min.js";

const CodePanel = () => {
  const { currentCode } = useStateContext();
  const [language, setLanguage] = useState("javascript");
  const codeRef = useRef(null);

  // Simple auto-detection logic
  useEffect(() => {
    if (!currentCode) {
      setLanguage("javascript");
      return;
    }

    const codeLower = currentCode.trim().toLowerCase();

    if (codeLower.includes("<html") || codeLower.includes("<div")) {
      setLanguage("markup"); // Prism's HTML name
    } else if (codeLower.includes("def ") || codeLower.includes("import ")) {
      setLanguage("python");
    } else if (codeLower.includes("classname=") || codeLower.includes("<")) {
      setLanguage("jsx");
    } else if (codeLower.includes("{") && codeLower.includes("}")) {
      setLanguage("javascript");
    } else {
      setLanguage("javascript");
    }
  }, [currentCode]);

  // Highlight when code or language changes
  useEffect(() => {
    if (codeRef.current) {
      Prism.highlightElement(codeRef.current);
    }
  }, [currentCode, language]);

  const styles = {
    container: {
      overflowY: "auto",
      height: "100%",
      padding: 10,
      backgroundColor: "#1e1e1e",
      color: "#d4d4d4",
      fontFamily: "'Source Code Pro', monospace",
      fontSize: 14,
      borderRadius: 4,
      boxSizing: "border-box",
      userSelect: "text",
      whiteSpace: "pre-wrap",
      wordBreak: "break-word",
      outline: "none",
    },
  };

  return (
    <section
      className="code-panel"
      style={styles.container}
      aria-label="Code display panel"
      tabIndex={0}
    >
      <pre>
        <code ref={codeRef} className={`language-${language}`}>
          {currentCode || "Click a button in Output Panel to see code here."}
        </code>
      </pre>
    </section>
  );
};

export default CodePanel;
