import React from "react";
import { useStateContext } from "../context/StateContext";

export default function OutputPanel() {
  const { workflowSteps, selectedStep, selectStep, isLoading } = useStateContext();

  return (
    <div style={{ border: "1px solid #ccc", padding: 10, marginBottom: 10 }}>
      <h3>Output Panel (Workflow)</h3>
      {workflowSteps.length === 0 && <p>No steps added yet</p>}
      {workflowSteps.map(({ id, name }) => (
        <div
          key={id}
          onClick={() => selectStep(id)}
          style={{
            cursor: "pointer",
            padding: "5px 10px",
            marginBottom: 5,
            border: id === selectedStep ? "2px solid blue" : "1px solid gray",
            backgroundColor: id === selectedStep ? "#ddf" : "#fff",
          }}
        >
          {name}
        </div>
      ))}
      {isLoading && <p>Loading...</p>}
    </div>
  );
}
