import React, { useRef } from 'react';
import axios from 'axios';
import { useStateContext } from '../context/StateContext';

const ActionPanel = () => {
  const { addWorkflowStep, addMessage } = useStateContext();
  const fileInputRef = useRef(null);

  // Handle File Upload
  const handleFileUpload = async (event) => {
    const file = event.target.files[0];
    if (!file) return;

    const formData = new FormData();
    formData.append('file', file);

    try {
      console.log('Uploading file:', file.name);
      const response = await axios.post('/api/upload', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });
      addMessage(response.data.message || 'File uploaded successfully');
      addWorkflowStep('Upload');
    } catch (error) {
      addMessage(error.response?.data?.message || 'File upload failed');
      console.error('Upload error:', error);
    }
  };

  // Generic handler creator for backend steps
  const createHandler = (stepName, apiPath, successMsg, failMsg) => async () => {
    try {
      console.log(`Calling API for step: ${stepName}`);
      const response = await axios.post(apiPath);
      addMessage(response.data.message || successMsg);
      addWorkflowStep(stepName);
    } catch (error) {
      addMessage(error.response?.data?.message || failMsg);
      console.error(`${stepName} error:`, error);
    }
  };

  return (
    <div className="action-panel" style={{ padding: '10px' }}>
      <h2>Action Panel</h2>

      {/* File Upload Section */}
      <div>
        <input
          type="file"
          ref={fileInputRef}
          onChange={handleFileUpload}
          style={{ display: 'none' }}
        />
        <button onClick={() => fileInputRef.current.click()}>Upload File</button>
      </div>

      {/* Action Buttons */}
      <div className="actions" style={{ marginTop: '20px', display: 'flex', flexDirection: 'column', gap: '10px' }}>
        <button onClick={createHandler('ConvertToCSV', '/api/convert_to_csv', 'Converted to CSV', 'Conversion failed')}>Convert to CSV</button>

        <button onClick={createHandler('CleanModify', '/api/clean_modify', 'Data cleaned', 'Clean & Modify failed')}>
          Clean & Modify
        </button>

        <button onClick={createHandler('FDModified', '/api/fd_modified', 'Functional dependencies detected', 'FD detection failed')}>
          Detect Functional Dependencies
        </button>

        <button onClick={createHandler('NormalizeTable', '/api/normalize_table', 'Tables normalized', 'Normalization failed')}>
          Normalize Tables
        </button>

        <button onClick={createHandler('KeyDetection', '/api/key_detection', 'Keys detected', 'Key detection failed')}>
          Detect Keys
        </button>

        <button onClick={createHandler('DependencyPreservation', '/api/dependency_preservation', 'Dependency preservation checked', 'Dependency preservation check failed')}>
          Check Dependency Preservation
        </button>

        <button onClick={createHandler('LosslessCheck', '/api/lossless_check', 'Lossless decomposition checked', 'Lossless check failed')}>
          Check Lossless Decomposition
        </button>

        <button onClick={createHandler('ERDiagram', '/api/generate_er_diagram', 'ER Diagram generated', 'ER Diagram generation failed')}>
          Generate ER Diagram
        </button>
      </div>
    </div>
  );
};

export default ActionPanel;
