import React from 'react';
import { useStateContext } from '../context/StateContext';
import { useNavigate } from 'react-router-dom';
import './MessagePanel.css';

const MessagePanel = () => {
  const { messages, normalizedTables } = useStateContext();
  const navigate = useNavigate();

  const handleTableClick = (tableName) => {
    navigate(`/table/${encodeURIComponent(tableName)}`);
  };

  return (
    <div className="message-panel">
      <h3>Messages & Tables</h3>
      <div className="messages-container">
        {messages.map((msg, index) => (
          <div key={index} className={`message ${msg.includes('✅') ? 'success' : msg.includes('❌') ? 'error' : ''}`}>
            {msg}
          </div>
        ))}
      </div>
      
      {normalizedTables.length > 0 && (
        <div className="tables-section">
          <h4>Available Tables</h4>
          <div className="table-buttons">
            {normalizedTables.map((table) => (
              <button
                key={table}
                onClick={() => handleTableClick(table)}
                className="table-button"
              >
                {table}
              </button>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default MessagePanel;
