import React, { useEffect, useRef, useState } from "react";
import mermaid from "mermaid";

const STORAGE_KEY = "erDiagramPositions";

const ERDiagramPage = () => {
  const [mermaidCode, setMermaidCode] = useState("");
  const [error, setError] = useState(null);
  const containerRef = useRef(null);

  const [positions, setPositions] = useState({});

  // Load positions from localStorage on mount
  useEffect(() => {
    const savedPositions = localStorage.getItem(STORAGE_KEY);
    if (savedPositions) {
      try {
        setPositions(JSON.parse(savedPositions));
      } catch {
        // ignore parse errors
      }
    }
  }, []);

  // Fetch Mermaid code on mount
  useEffect(() => {
    fetch("http://localhost:5000/api/er_diagram")
      .then(async (res) => {
        if (!res.ok) {
          const text = await res.text();
          throw new Error(`Server Error ${res.status}: ${text}`);
        }
        return res.json();
      })
      .then((data) => {
        if (data.mermaid_code) setMermaidCode(data.mermaid_code);
        else if (data.error) throw new Error(data.error);
        else throw new Error("No Mermaid code received");
      })
      .catch((e) => setError(e.message));
  }, []);

  // Render Mermaid diagram when code or positions update
  useEffect(() => {
    if (!mermaidCode || !containerRef.current) return;

    mermaid.initialize({ startOnLoad: false });
    containerRef.current.innerHTML = "";

    mermaid
      .render("mermaidDiagram", mermaidCode)
      .then(({ svg }) => {
        if (!containerRef.current) return;
        containerRef.current.innerHTML = svg;

        applyPositions();
        enableDrag();
      })
      .catch((e) => setError("Mermaid render error: " + e.message));
  }, [mermaidCode, positions]);

  // Apply stored positions to nodes using transform
  const applyPositions = () => {
    const svgContainer = containerRef.current;
    if (!svgContainer) return;

    const nodes = svgContainer.querySelectorAll("g.node");
    nodes.forEach((node) => {
      const id = node.id || node.getAttribute("data-id") || node.querySelector("title")?.textContent;
      if (!id) return;

      const pos = positions[id];
      if (pos) {
        node.setAttribute("transform", `translate(${pos.x},${pos.y})`);
      }
    });
  };

  // Drag & Drop handlers with localStorage saving
  const enableDrag = () => {
    const svgContainer = containerRef.current;
    if (!svgContainer) return;

    const nodes = svgContainer.querySelectorAll("g.node");

    nodes.forEach((node) => {
      let isDragging = false;
      let startX, startY;
      let origX = 0,
        origY = 0;

      node.style.cursor = "grab";

      const id =
        node.id || node.getAttribute("data-id") || node.querySelector("title")?.textContent;
      if (!id) return;

      const onMouseDown = (e) => {
        isDragging = true;
        startX = e.clientX;
        startY = e.clientY;

        const transform = node.getAttribute("transform");
        const match = /translate\(([-\d.]+),\s*([-\d.]+)\)/.exec(transform);
        origX = match ? parseFloat(match[1]) : 0;
        origY = match ? parseFloat(match[2]) : 0;

        node.style.cursor = "grabbing";
        e.preventDefault();
      };

      const onMouseMove = (e) => {
        if (!isDragging) return;

        const dx = e.clientX - startX;
        const dy = e.clientY - startY;

        const newX = origX + dx;
        const newY = origY + dy;

        node.setAttribute("transform", `translate(${newX},${newY})`);

        e.preventDefault();
      };

      const onMouseUp = (e) => {
        if (!isDragging) return;

        isDragging = false;
        node.style.cursor = "grab";

        const transform = node.getAttribute("transform");
        const match = /translate\(([-\d.]+),\s*([-\d.]+)\)/.exec(transform);
        const newX = match ? parseFloat(match[1]) : 0;
        const newY = match ? parseFloat(match[2]) : 0;

        setPositions((prev) => {
          const newPos = { ...prev, [id]: { x: newX, y: newY } };
          localStorage.setItem(STORAGE_KEY, JSON.stringify(newPos)); // Save to localStorage here
          return newPos;
        });

        e.preventDefault();
      };

      node.addEventListener("mousedown", onMouseDown);
      window.addEventListener("mousemove", onMouseMove);
      window.addEventListener("mouseup", onMouseUp);
    });
  };

  if (error) return <div style={{ color: "red" }}>Error: {error}</div>;
  if (!mermaidCode) return <div>Loading ER Diagram...</div>;

  return (
    <div style={{ padding: "1rem", overflow: "auto" }}>
      <h2>ER Diagram (Drag tables to reposition)</h2>
      <div
        ref={containerRef}
        style={{ border: "1px solid #ccc", minHeight: "500px", cursor: "default" }}
      />
    </div>
  );
};

export default ERDiagramPage;
