import React, { useRef } from "react";
import { useStateContext } from "../context/StateContext";

const ActionPanel = ({ onAddBlock }) => {
  const { resetWorkflow } = useStateContext();
  const fileInputRef = useRef();

  const steps = [
    { label: "Upload File", value: "UploadFile" },
    { label: "Convert to CSV", value: "ConvertCSV" },
    { label: "Clean Modify", value: "cleanModify" },
    { label: "FD Modified", value: "fdModified" },
    { label: "Normalized Table", value: "normalizedTable" },
    { label: "Key Detection", value: "Keys" },
    { label: "Dependency Check", value: "dependencyCheck" },
    { label: "Lossless Check", value: "losslessCheck" },
    { label: "ER Diagram", value: "erDiagram" },
  ];

  const handleUpload = () => {
    const file = fileInputRef.current.files[0];
    if (file) {
      onAddBlock("UploadFile", file);
    }
  };

  return (
    <div>
      <h3>Actions</h3>
      <input type="file" ref={fileInputRef} />
      <button onClick={handleUpload}>Add Upload File</button>
      {steps.slice(1).map((step) => (
        <button key={step.value} onClick={() => onAddBlock(step.value)}>
          {step.label}
        </button>
      ))}
      <button onClick={resetWorkflow} style={{ marginTop: "20px" }}>
        Reset Workflow
      </button>
    </div>
  );
};

export default ActionPanel;
