import React, { useRef } from 'react';
import { uploadFile, triggerBackendStep, checkDependencyPreservation } from '../api';
import { useStateContext } from '../context/StateContext';

const ActionPanel = () => {
  const {
    addMessage,
    addWorkflowStep,
    fetchNormalizedTables,
    originalFDs,
    decomposedSchemas,
    fetchOriginalFDs,
    fetchDecomposedSchemas,
  } = useStateContext();

  const fileInputRef = useRef(null);

  // Handle File Upload
  const handleFileUpload = async (event) => {
    const file = event.target.files[0];
    if (!file) return;

    try {
      const message = await uploadFile(file);
      addMessage(message);
      addWorkflowStep('Upload');
    } catch (error) {
      addMessage(error.message || 'Failed to upload file');
    }
  };

  // Generic Backend Step Handler
  const handleBackendAction = async (stepName) => {
    try {
      const message = await triggerBackendStep(stepName);
      addMessage(message);
      addWorkflowStep(stepName);

      // After FD Detection, fetch Functional Dependencies
      if (stepName === 'FDModified') {
        await fetchOriginalFDs();
        addMessage('Fetched Functional Dependencies');
      }

      // After Normalization, fetch Decomposed Schemas + Normalized Tables
      if (stepName === 'NormalizeTable') {
        await fetchDecomposedSchemas();
        addMessage('Fetched Decomposed Schemas');
        await fetchNormalizedTables();
        addMessage('Fetched Normalized Tables');
      }

    } catch (error) {
      addMessage(error.message || `Failed at step: ${stepName}`);
    }
  };

  // Dependency Preservation Check
  const handleDependencyPreservation = async () => {
    try {
      // Ensure latest FDs and Schemas fetched
      if (originalFDs.length === 0) {
        await fetchOriginalFDs();
        addMessage('Refetched Functional Dependencies (just in case)');
      }
      if (decomposedSchemas.length === 0) {
        await fetchDecomposedSchemas();
        addMessage('Refetched Decomposed Schemas (just in case)');
      }

      // Validate after fetching
      if (!originalFDs || originalFDs.length === 0) {
        addMessage('Error: Functional Dependencies are missing or invalid.');
        return;
      }
      if (!decomposedSchemas || decomposedSchemas.length === 0) {
        addMessage('Error: Decomposed Schemas are missing or invalid.');
        return;
      }

      // Perform Dependency Preservation Check
      const message = await checkDependencyPreservation(originalFDs, decomposedSchemas);
      addMessage(message);
      addWorkflowStep('DependencyPreservation');
    } catch (error) {
      addMessage(error.message || 'Unknown error during Dependency Preservation');
    }
  };

  return (
    <div className="action-panel">
      <h2>Action Panel</h2>

      {/* File Upload */}
      <div>
        <input
          type="file"
          ref={fileInputRef}
          onChange={handleFileUpload}
          style={{ display: 'none' }}
        />
        <button onClick={() => fileInputRef.current.click()}>Upload File</button>
      </div>

      {/* Workflow Actions */}
      <div className="actions">
        <button onClick={() => handleBackendAction('ConvertToCSV')}>Convert to CSV</button>
        <button onClick={() => handleBackendAction('CleanModify')}>Clean & Modify</button>
        <button onClick={() => handleBackendAction('FDModified')}>Detect Functional Dependencies</button>
        <button onClick={() => handleBackendAction('KeyDetection')}>Detect Keys</button>
        <button onClick={() => handleBackendAction('NormalizeTable')}>Normalize Tables</button>
        <button onClick={handleDependencyPreservation}>Check Dependency Preservation</button>
        <button onClick={() => handleBackendAction('LosslessCheck')}>Check Lossless Decomposition</button>
        <button onClick={() => handleBackendAction('ERDiagram')}>Generate ER Diagram</button>
      </div>
    </div>
  );
};

export default ActionPanel;
