import React from 'react';
import { useStateContext } from '../context/StateContext';

const CodePanel = () => {
  const { codeSnippet, isLoading } = useStateContext();

  return (
    <div className="code-panel">
      <h2>Code Snippet</h2>
      <div
        style={{
          height: '300px',
          overflowY: 'auto',
          backgroundColor: '#f7f7f7',
          padding: '10px',
          border: '1px solid #ddd',
          borderRadius: '6px',
          fontFamily: 'monospace',
          whiteSpace: 'pre-wrap'
        }}
      >
        {isLoading ? (
          <p>Loading Code...</p>
        ) : (
          <code>{codeSnippet || 'Click a workflow block to view code here.'}</code>
        )}
      </div>
    </div>
  );
};

export default CodePanel;
