// src/App.jsx
import React from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";

// src/api.js or at the top of your project entry (e.g., index.jsx or App.jsx)
import axios from "axios";

axios.defaults.baseURL = "http://localhost:5000"; // adjust as needed


import ActionPanel from "./components/ActionPanel";
import OutputPanel from "./components/OutputPanel";
import CodePanel from "./components/CodePanel";
import MessagePanel from "./components/messagePanel";
import NormalizedTableView from "./components/NormalizedTableView";

import { StateProvider } from "./context/StateContext";

const App = () => {
  const { addStep } = useStateContext(); // Add this line to get addStep from context

  return (
    <StateProvider>
      <Router>
        <Routes>
          <Route
            path="/"
            element={
              <div
                style={{
                  display: "grid",
                  gridTemplateColumns: "250px 1fr 350px 350px",
                  gap: "10px",
                  height: "100vh",
                  padding: "10px",
                }}
              >
                <ActionPanel onAddBlock={addStep} /> {/* Pass addStep as onAddBlock */}
                <OutputPanel />
                <CodePanel />
                <MessagePanel />
              </div>
            }
          />
          <Route path="/table/:tableName" element={<NormalizedTableView />} />
        </Routes>
      </Router>
    </StateProvider>
  );
};
