import React, { useEffect, useState, useRef } from 'react';
import { useStateContext } from '../context/StateContext';
import DraggableStep from './DraggableStep';
import axios from 'axios';

const STORAGE_KEY = 'workflowStepPositions';

const OutputPanel = () => {
  const { workflowSteps, setCurrentCode } = useStateContext();
  const [positions, setPositions] = useState({});
  const [boxWidths, setBoxWidths] = useState({});
  const [scale, setScale] = useState(1);
  const containerRef = useRef(null);

  const [controlPoints, setControlPoints] = useState({});
  const positionsInitialized = useRef(false);

  // Undo / Redo stacks
  const [undoStack, setUndoStack] = useState([]);
  const [redoStack, setRedoStack] = useState([]);

  // Current focused step index for step-by-step navigation
  const [currentStepIndex, setCurrentStepIndex] = useState(null);

  // Track which steps are "removed" (hidden)
  const [removedSteps, setRemovedSteps] = useState(new Set());

  // Flag to track if any action panel button was clicked
  const [actionPanelClicked, setActionPanelClicked] = useState(false);

  const boxHeight = 44;
  const horizontalSpacing = 60;
  const verticalSpacing = 60;

  // Map internal step IDs to user-friendly display names
  const stepDisplayNames = {
    Upload: 'Upload',
    CleanModify: 'Noise Clean',
    FDModified: 'FD Detection',
    NormalizeTable: 'Normalize to 3NF',
    LosslessCheck: 'Lossless Decomposition',
    ERDiagram: 'ER Diagram',
    ConvertToCSV: 'CSV Converter',
    KeyDetection: 'Key Detector',
    DependencyPreservation: 'Check Dependency Preservation',
  };

  // Load saved positions on mount
  useEffect(() => {
    const savedPositions = localStorage.getItem(STORAGE_KEY);
    if (savedPositions) {
      try {
        const parsedPositions = JSON.parse(savedPositions);
        setPositions(parsedPositions);
        positionsInitialized.current = true;
      } catch {
        console.warn('Failed to parse saved positions');
      }
    }
  }, []);

  // Initialize default positions if none saved, after we know box widths
  useEffect(() => {
    if (positionsInitialized.current) return;
    if (!containerRef.current) return;
    if (workflowSteps.length === 0) return;

    // Wait until all box widths are measured for all workflowSteps
    if (workflowSteps.some((step) => !boxWidths[step])) return;

    const containerWidth = containerRef.current.clientWidth;
    const initialPositions = {};

    let x = 50;
    let y = 60;

    workflowSteps.forEach((step) => {
      const width = boxWidths[step] || 140;

      if (x + width > containerWidth - 20) {
        x = 50;
        y += boxHeight + verticalSpacing;
      }
      initialPositions[step] = { x, y };
      x += width + horizontalSpacing;
    });

    setPositions((prev) => ({ ...prev, ...initialPositions }));
    positionsInitialized.current = true;
  }, [workflowSteps, boxWidths]);

  // Save positions to localStorage when changed
  useEffect(() => {
    if (positionsInitialized.current) {
      localStorage.setItem(STORAGE_KEY, JSON.stringify(positions));
    }
  }, [positions]);

  // Ensure currentStepIndex stays valid when workflowSteps change
  useEffect(() => {
    if (currentStepIndex == null) return;
    if (currentStepIndex >= workflowSteps.length) {
      setCurrentStepIndex(workflowSteps.length - 1);
    }
  }, [workflowSteps, currentStepIndex]);

  // Handle drag of step boxes with undo stack update
  const handlePositionChange = (step, pos) => {
    setPositions((prev) => {
      setUndoStack((undoPrev) => [...undoPrev, prev]);
      setRedoStack([]);
      return { ...prev, [step]: pos };
    });
  };

  const handleClickStep = async (step) => {
    try {
      const response = await axios.get(`/api/code/${step}`);
      setCurrentCode(response.data.code || '');
    } catch {
      setCurrentCode('Failed to load code');
    }

    const idx = workflowSteps.indexOf(step);
    if (idx !== -1) setCurrentStepIndex(idx);
  };

  // Undo handler: undo 2 steps at once
  const handleUndo = () => {
    if (undoStack.length === 0) return;

    setPositions((currentPositions) => {
      let newUndoStack = [...undoStack];
      let newRedoStack = [...redoStack, currentPositions];

      let previousPositions;
      for (let i = 0; i < 2; i++) {
        if (newUndoStack.length === 0) break;
        previousPositions = newUndoStack.pop();
        newRedoStack.push(previousPositions);
      }

      setUndoStack(newUndoStack);
      setRedoStack(newRedoStack);

      return previousPositions || currentPositions;
    });
  };

  // Redo handler: redo 2 steps at once
  const handleRedo = () => {
    if (redoStack.length === 0) return;

    setPositions((currentPositions) => {
      let newRedoStack = [...redoStack];
      let newUndoStack = [...undoStack, currentPositions];

      let nextPositions;
      for (let i = 0; i < 2; i++) {
        if (newRedoStack.length === 0) break;
        nextPositions = newRedoStack.pop();
        newUndoStack.push(nextPositions);
      }

      setRedoStack(newRedoStack);
      setUndoStack(newUndoStack);

      return nextPositions || currentPositions;
    });
  };

  // Helper cubic Bézier function for point at parameter t
  function cubicBezierPoint(t, P0, P1, P2, P3) {
    const u = 1 - t;
    const tt = t * t;
    const uu = u * u;
    const uuu = uu * u;
    const ttt = tt * t;

    const x =
      uuu * P0.x +
      3 * uu * t * P1.x +
      3 * u * tt * P2.x +
      ttt * P3.x;
    const y =
      uuu * P0.y +
      3 * uu * t * P1.y +
      3 * u * tt * P2.y +
      ttt * P3.y;

    return { x, y };
  }

  const renderLines = () => {
    const dotRadius = 4.5;
    const lines = [];

    const t1 = 0.25; // parameter for first dot on curve
    const t2 = 0.75; // parameter for second dot on curve

    for (let i = 0; i < workflowSteps.length - 1; i++) {
      const from = positions[workflowSteps[i]];
      const to = positions[workflowSteps[i + 1]];

      // Skip drawing lines if either box is removed
      if (removedSteps.has(workflowSteps[i]) || removedSteps.has(workflowSteps[i + 1])) {
        continue;
      }

      if (from && to) {
        const fromWidth = boxWidths[workflowSteps[i]] || 140;

        const padding = 6; // padding so lines don't overlap box edges

        // Start point at right edge center of "from" box
        const P0 = {
          x: from.x + fromWidth + padding,
          y: from.y + boxHeight / 2,
        };

        // End point at left edge center of "to" box
        const P3 = {
          x: to.x - padding,
          y: to.y + boxHeight / 2,
        };

        const cp = controlPoints[i] || {};

        let P1, P2;

        if (cp.c1 && cp.c2) {
          P1 = cp.c1;
          P2 = cp.c2;
        } else {
          if (Math.abs(P3.x - P0.x) > Math.abs(P3.y - P0.y)) {
            P1 = { x: P0.x + (P3.x - P0.x) / 2, y: P0.y };
            P2 = { x: P0.x + (P3.x - P0.x) / 2, y: P3.y };
          } else {
            P1 = { x: P0.x, y: P0.y + (P3.y - P0.y) / 2 };
            P2 = { x: P3.x, y: P0.y + (P3.y - P0.y) / 2 };
          }
        }

        // Compute dot positions on the curve
        const dot1Pos = cubicBezierPoint(t1, P0, P1, P2, P3);
        const dot2Pos = cubicBezierPoint(t2, P0, P1, P2, P3);

        // Build path string
        const d = `M${P0.x},${P0.y} C${P1.x},${P1.y} ${P2.x},${P2.y} ${P3.x},${P3.y}`;

        lines.push(
          <path
            key={`line-${i}`}
            d={d}
            fill="none"
            stroke="rgba(184, 212, 249, 0.9)"
            strokeWidth="1.5"
            markerEnd="url(#arrowhead)"
          />
        );

        // Draggable dots placed on curve at dot1Pos and dot2Pos
        lines.push(
          <circle
            key={`dot1-${i}`}
            cx={dot1Pos.x}
            cy={dot1Pos.y}
            r={dotRadius}
            fill="rgba(53, 143, 215, 0.9)"
            stroke="white"
            strokeWidth="1"
            cursor="move"
            onPointerDown={(e) => {
              e.stopPropagation();
              const startPos = { x: e.clientX, y: e.clientY };
              const initialC1 = P1;
              const initialC2 = P2;

              const onPointerMove = (ev) => {
                ev.preventDefault();

                // Calculate how far pointer moved relative to start
                const dx = ev.clientX - startPos.x;
                const dy = ev.clientY - startPos.y;

                // Update control points based on which dot is dragged
                setControlPoints((prev) => {
                  const newControlPoints = { ...prev };
                  const cpCopy = { ...newControlPoints[i] };

                  if (ev.target === ev.currentTarget) {
                    // Determine if dot1 or dot2 by key in the event target? We'll just check circle keys
                    // This event handler is shared - to differentiate we need a better way
                  }

                  // Because this handler is shared, only handle for dot1 here
                  cpCopy.c1 = { x: initialC1.x + dx, y: initialC1.y + dy };
                  newControlPoints[i] = cpCopy;

                  return newControlPoints;
                });
              };

              const onPointerUp = () => {
                window.removeEventListener('pointermove', onPointerMove);
                window.removeEventListener('pointerup', onPointerUp);
              };

              window.addEventListener('pointermove', onPointerMove);
              window.addEventListener('pointerup', onPointerUp);
            }}
          />,
          <circle
            key={`dot2-${i}`}
            cx={dot2Pos.x}
            cy={dot2Pos.y}
            r={dotRadius}
            fill="rgba(53, 143, 215, 0.9)"
            stroke="white"
            strokeWidth="1"
            cursor="move"
            onPointerDown={(e) => {
              e.stopPropagation();
              const startPos = { x: e.clientX, y: e.clientY };
              const initialC1 = P1;
              const initialC2 = P2;

              const onPointerMove = (ev) => {
                ev.preventDefault();

                const dx = ev.clientX - startPos.x;
                const dy = ev.clientY - startPos.y;

                setControlPoints((prev) => {
                  const newControlPoints = { ...prev };
                  const cpCopy = { ...newControlPoints[i] };

                  // For dot2 drag
                  cpCopy.c2 = { x: initialC2.x + dx, y: initialC2.y + dy };
                  newControlPoints[i] = cpCopy;

                  return newControlPoints;
                });
              };

              const onPointerUp = () => {
                window.removeEventListener('pointermove', onPointerMove);
                window.removeEventListener('pointerup', onPointerUp);
              };

              window.addEventListener('pointermove', onPointerMove);
              window.addEventListener('pointerup', onPointerUp);
            }}
          />
        );
      }
    }

    return (
      <svg
        style={{ position: 'absolute', top: 0, left: 0, width: '100%', height: '100%', pointerEvents: 'none' }}
        xmlns="http://www.w3.org/2000/svg"
      >
        <defs>
          <marker
            id="arrowhead"
            markerWidth="10"
            markerHeight="7"
            refX="0"
            refY="3.5"
            orient="auto"
            fill="rgba(184, 212, 249, 0.9)"
          >
            <polygon points="0 0, 10 3.5, 0 7" />
          </marker>
        </defs>
        {lines}
      </svg>
    );
  };

  // Previous step (reverse button) handler
  const prevStep = () => {
    if (workflowSteps.length === 0) return;

    if (currentStepIndex == null) {
      // If no current index, remove last step
      const lastIndex = workflowSteps.length - 1;
      const stepToRemove = workflowSteps[lastIndex];
      setRemovedSteps((prev) => new Set(prev).add(stepToRemove));
      setCurrentStepIndex(lastIndex - 1 >= 0 ? lastIndex - 1 : null);
    } else if (currentStepIndex >= 0) {
      const stepToRemove = workflowSteps[currentStepIndex];
      setRemovedSteps((prev) => new Set(prev).add(stepToRemove));
      setCurrentStepIndex(currentStepIndex - 1 >= 0 ? currentStepIndex - 1 : null);
    }

    setActionPanelClicked(false); // Reset action panel flag on reverse
  };

  // Next step (forward button) handler
  const nextStep = () => {
    if (workflowSteps.length === 0) return;

    const nextIndex = currentStepIndex == null ? 0 : currentStepIndex + 1;
    if (nextIndex >= workflowSteps.length) return;

    const step = workflowSteps[nextIndex];

    if (actionPanelClicked) {
      // If action panel clicked, do NOT restore removed step
      // Just set current step index and action panel box will show
      setCurrentStepIndex(nextIndex);
    } else {
      // Restore removed step if it was removed
      setRemovedSteps((prev) => {
        const newSet = new Set(prev);
        newSet.delete(step);
        return newSet;
      });
      setCurrentStepIndex(nextIndex);
    }
  };

  // Action panel button click handler
  // Call this in your actual action panel buttons
  const onActionPanelButtonClick = () => {
    setActionPanelClicked(true);
    // Optionally set currentStepIndex to the action panel box index, if applicable
  };

  // Update box width measurements when size changes
  const handleSizeChange = (step, size) => {
    setBoxWidths((prev) => ({ ...prev, [step]: size.width }));
  };

  // Handle zoom slider change
  const handleZoomChange = (event) => {
    const value = Number(event.target.value);
    if (value < 0.5) setScale(0.5);
    else if (value > 2) setScale(2);
    else setScale(value);
  };

  return (
    <div
      ref={containerRef}
      style={{
        position: 'relative',
        width: '100%',
        height: '100%',
        overflow: 'auto',
        userSelect: 'none',
        touchAction: 'none',
      }}
    >
      {/* Zoom control */}
      <div style={{ padding: '6px', position: 'fixed', top: 10, left: 10, background: 'white', zIndex: 1000 }}>
        <button onClick={handleUndo} disabled={undoStack.length === 0}>
          Undo
        </button>
        <button onClick={handleRedo} disabled={redoStack.length === 0}>
          Redo
        </button>
        <button onClick={prevStep} disabled={currentStepIndex === null && workflowSteps.length === 0}>
          Reverse Step
        </button>
        <button onClick={nextStep} disabled={currentStepIndex === workflowSteps.length - 1 || workflowSteps.length === 0}>
          Forward Step
        </button>
        <label style={{ marginLeft: 10 }}>
          Zoom:
          <input
            type="range"
            min="0.5"
            max="2"
            step="0.01"
            value={scale}
            onChange={handleZoomChange}
            style={{ marginLeft: 6 }}
          />
        </label>
      </div>

      {/* Render lines */}
      {renderLines()}

      {/* Render draggable step boxes */}
      <div
        style={{
          position: 'relative',
          width: '100%',
          height: '100%',
          transform: `scale(${scale})`,
          transformOrigin: 'top left',
        }}
      >
        {workflowSteps.map((step, index) => {
          if (removedSteps.has(step)) return null; // Hide removed steps

          return (
            <DraggableStep
              key={step}
              step={step}
              displayName={stepDisplayNames[step] || step}
              position={positions[step] || { x: 0, y: 0 }}
              onPositionChange={handlePositionChange}
              onClick={handleClickStep}
              isFirst={index === 0}
              onSizeChange={handleSizeChange}
              isActive={index === currentStepIndex}
            />
          );
        })}

        {/* Optionally render action panel box if actionPanelClicked is true */}
        {actionPanelClicked && (
          <div
            style={{
              position: 'absolute',
              top: 10,
              left: 10,
              padding: 10,
              background: '#f0f8ff',
              border: '2px solid #3590d7',
              borderRadius: 6,
              zIndex: 2000,
            }}
          >
            {/* Replace this with your actual action panel UI */}
            <p>Action Panel Box</p>
            <button
              onClick={() => {
                // Example: close action panel and clear flag
                setActionPanelClicked(false);
              }}
            >
              Close
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default OutputPanel;
